```typescript
import { decode } from "./base64.js";

decode("SGVsbG8sIFdvcmxkIQ==");
// => "Hello, World!"

decode("SGVsbG8sIFdvcmxkIQ==", "standard");
// => "Hello, World!"
```

```typescript
import { decode58 } from "./base58.js";

decode58("JxF12TrwUP45BMd");
// => "Hello World"

decode58("jXf12sRWto45bmD", "flickr");
// => "Hello World"
```

```typescript
import { encode } from "./base64.js";

encode("Hello, World!");
// => "SGVsbG8sIFdvcmxkIQ=="

encode("Hello, World!", "urlsafe");
// => "SGVsbG8sIFdvcmxkIQ=="
```

```typescript
import { encode58 } from "./base58.js";

encode58("Hello World");
// => "JxF12TrwUP45BMd"

encode58("Hello World", "flickr");
// => "jXf12sRWto45bmD"
```

